public class SocialBot
{
   private String socialboturl;
   private String username;
   private String password;

   private HttpClient client;
   private HttpRequest request;
   private HttpResponse response;

   public SocialBot(String socialboturlneu, String usernameneu, String passwordneu)
   {
      socialboturl = socialboturlneu;
      username = usernameneu;
      password = passwordneu;
      client = new HttpClient();
      request = new HttpRequest();
   }

   public JsonElement abfragen(String api, String parameters)
   {
      request.uri(socialboturl + api + URLEncoder.encode("?" + parameters));
      response = client.send(request);
      // Kontrollausgabe
      println(response.body());
      return JsonParser.parse(response.body());
   }

   public void senden(String api, String parameters)
   {
      request.uri(socialboturl + api);
      request.POST(URLEncoder.encode("username=" + username + "&password=" + password + "&" + parameters));
      response = client.send(request);
      // Kontrollausgabe
      println(response.body());
   }
}

public class Satzgenerator {
   private ArrayList<String> subjects;
   private ArrayList<String> verbs;
   private ArrayList<String> objects;

   Satzgenerator()
   {
      subjects = new ArrayList<>();
      String text1 = Files.read("01subjects.txt");
      String[] zeilen1 = text1.split("\n");
      for (String text : zeilen1) subjects.add(text);

      verbs = new ArrayList<>();
      String text2 = Files.read("02verbs.txt");
      String[] zeilen2 = text2.split("\n");
      for (String text : zeilen2) verbs.add(text);

      objects = new ArrayList<>();
      String text3 = Files.read("03objects.txt");
      String[] zeilen3 = text3.split("\n");
      for (String text : zeilen3) objects.add(text);
   }

   public String satzErzeugen() 
   {
      int subjectIndex = Random.randint(0, subjects.size() - 1);
      int verbIndex = Random.randint(0, verbs.size() - 1);
      int objectIndex = Random.randint(0, objects.size()-1);

      return subjects.get(subjectIndex) + " " + verbs.get(verbIndex) + " " + objects.get(objectIndex) + ".";
   }
}

public class MeinBot extends SocialBot
{
   public MeinBot()
   {
      super("https://socialbotnet.de", "meinBot", "meinPasswort");
   }

   public void beitragSchreiben(String beitrag)
   {
      senden("/api/post", "message=" + beitrag);
   }

   public void userlisteAusgeben()
   {
      JsonElement answer = abfragen("/api/users", "sortby=id&order=asc&limit=10");
      for (JsonElement object : answer.getArrayValues()) 
      {
         println(object.getAsString("username"));
      }
   }

   public void beitraegeAusgeben()
   {
      JsonElement answer = abfragen("/api/posts", "sortby=id&order=asc&limit=10");
      for (JsonElement object : answer.getArrayValues()) 
      {
         JsonElement userdata = object.getAttributeValue("user");
         println(object.getAsString("message") + " von " + userdata.getAsString("username"));
      }
   }
}

